{let t=()=>{let t=$("#new-post-form");t.submit((function(s){s.preventDefault(),$.ajax({type:"post",url:"/posts/create",data:t.serialize(),success:function(t){let s=e(t.data.post);$("#posts-list-container>ul").prepend(s),n($(" .delete-post-button",s))},error:t=>{console.log(t.responseText)}})}))},e=t=>$(`<li id="post-${t._id}">\n        \n        <p>\n            <small>\n                <a class="delete-post-button" href="/posts/destroy/${t._id}">delete post</a>\n            </small>\n    \n            ${t.content}\n        <br>\n        <small>\n        ${t.user.name}\n        </small>\n        </p>\n        <div class="post-comments">\n           \n                <form action="/comments/create" method="post" >\n                    <input type="text" name="content" placeholder="write comment">\n                    <input type="hidden" name="post" value="${t._id}">\n                    <input type="submit" value="Add comment">\n                </form>\n              \n    \n              <div class="post-comments-list">\n                  <ul id="post-comments-${t._id}">\n                      <h4>Comments</h4>\n                      \n                  </ul>\n              </div>  \n        </div>\n    \n    </li>`),n=t=>{$(t).click((e=>{e.preventDefault(),$.ajax({type:"get",url:$(t).prop("href"),success:function(t){$(`#post-${t.data.post_id}`).remove()},error:function(t){console.log(t.responseText)}})}))};t()}